<?php/** * The main template file * * This is the most generic template file in a WordPress theme * and one of the two required files for a theme (the other being style.css). * It is used to display a page when nothing more specific matches a query. * E.g., it puts together the home page when no home.php file exists. * * @link http://codex.wordpress.org/Template_Hierarchy * * @package WordPress * @subpackage Twenty_Sixteen * @since Twenty Sixteen 1.0 */get_header();?><div id="primary" class="content-area">    <main id="main" class="site-main" role="main">        <?php if ( have_posts() ) : ?>            <?php if ( is_home() && ! is_front_page() ) : ?>                <header>                    <h1 class="page-title screen-reader-text"><?php single_post_title(); ?></h1>                </header>            <?php endif; ?>            <?php            // Start the loop.            while ( have_posts() ) : the_post(); ?>            <h1><?php the_title(); ?></h1>                <?php the_content(); ?>                <h3>Célközönség:</h3>                <p> <?php the_field('celkozonseg'); ?></p>                <h3>Belépési feltételek:</h3>                <p><?php the_field('belepesi_feltetelek'); ?></p>                <h3>Igazolás:</h3>                <p><?php the_field('igazolas'); ?></p>                <h3>Listaár:</h3>                <p><?php the_field('listaar');?></p>                <h3>Akciós ár:</h3>                <p><?php the_field('akcios_ar'); ?></p>                <i>Részletfiztetés <?php the_field('reszletfizetes'); ?> részletben lehetséges.</i>                                <ul>                    <?php 					if(count((get_field('tematika')) > 0)):?>					<h3>Tematika:</h3> <?php 					foreach (get_field('tematika') as $tema) { ?>                        <li>                            <h4><?php echo $tema['cim']; ?></h4>                            <p><?php echo $tema['leiras']; ?></p>                        </li>                    <?php }					endif;                    ?>                </ul>                             <ul>                    <?php 					if(count(get_field('demovideok')) > 0 ): ?>					  <h3>Demovideok</h3>					  <?php 					foreach (get_field('demovideok') as $video) { ?>                        <li>                            <?php echo $video['link']; ?>                        </li>                    <?php } 					endif;                    ?>                </ul>                <h3>Tanfolyam kiírás:</h3>                <?php                $kiirasok = get_posts(array(                    'post_type' => 'tanfolyam-kiiras',                    'meta_query' => array(                        array(                            'key' => 'kapcsolodo_tanfolyam', // name of custom field                            'value' => '"' . get_the_ID() . '"', // matches exaclty "123", not just 123. This prevents a match for "1234"                            'compare' => 'LIKE'                        )                    )                ));                if(count($kiirasok) > 0){					foreach ($kiirasok as $kiiras) { ?>                    <div class="kiiras" style="border: 1px solid #000;padding: 15px">                        <h4><?php echo get_field('kezdes',$kiiras->ID); ?> - <?php echo get_field('vege',$kiiras->ID); ?></h4>                        <hr>                        <p><?php echo $kiiras->post_content; ?></p>                        <h4>Tanár:</h4>                        <p><?php echo get_post(get_field('tanar',$kiiras->ID)[0])->post_title; ?></p>                        <h4>Helyszín:</h4>                        <p><?php echo get_post(get_field('helyszin',$kiiras->ID)[0])->post_title; ?></p>                        <h4>Napszak:</h4>                        <p><?php echo get_field('napszak',$kiiras->ID); ?></p>                        <h4>Besztás:</h4>                        <p><?php echo get_field('beosztas',$kiiras->ID); ?></p>                        <h4>Minimum letszam:</h4>                        <p><?php echo get_field('minimum_letszam',$kiiras->ID); ?></p>                        <h4>Maximum létszám:</h4>                        <h4>Beiratkozva:</h4>                        <p><?php echo get_field('maximum letszam',$kiiras->ID); ?></p>                        <p><?php echo get_field('beiratkozva',$kiiras->ID); ?></p>												<?php if(get_field('maximum letszam',$kiiras->ID) > get_field('beiratkozva',$kiiras->ID)){ ?>							<a href="<?php echo get_permalink($kiiras->ID); ?>?jelentkezes"><button>Jelentkezés</button></a>						<?php }else{ ?>							<h3>A tanfolyam megtelt!</h3>							<a href="<?php echo get_permalink($kiiras->ID); ?>?varolista"><button>Várólistára feliratkozás</button></a>						<?php } ?>						<a href="<?php echo get_permalink($kiiras->ID); ?>?kerdes"><button>Kérdésem van a tanfolyammal kapcsolatban!</button></a>						<a href="<?php echo get_permalink($kiiras->ID); ?>?erdeklodes"><button>Érdekel a tanfolyam egy későbbi időpontban!</button></a>						<a href="<?php echo get_permalink($kiiras->ID); ?>?ceg"><button>Céges ajánlatkérés!</button></a>						                    </div>                <?php } 				}?>				                                            <?php endwhile;            // Previous/next page navigation.        // If no content, include the "No posts found" template.        else :            get_template_part( 'template-parts/content', 'none' );        endif;        ?>    </main><!-- .site-main --></div><!-- .content-area --><?php get_sidebar(); ?><?php get_footer(); ?>